data.logic:
var calc_output = [];
var u = parseInt(cut_down) + parseInt(annoyed) + parseInt(guilty) + parseInt(eye_opener);
var msg = '';
var mini_msg = '';
if (u <= 1) { msg += 'Screening negative.';
    mini_msg = 'Screening negative.' }
if (u > 1) { msg += 'Scores of 2 or higher had a 93% sensitivity/76% specificity for the identification of \u201cexcessive drinking\u201d and a 91% sensitivity/77% specificity for the identification of alcoholism.';
    mini_msg = 'High sensitivity for \u201cexcessive drinking\u201d and alcoholism.' }
if (eye_opener > 0) { msg += '<br/><br/>Some clinicians also consider the \u201ceye opener\u201d question as highly concerning for unhealthy drinking behavior, even if all other questions are answered negatively.' }
calc_output.push({ 
    name: 'mini', 
    value: u, 
    value_text: 'points', 
    message: mini_msg 
});
calc_output.push({ 
    name: '1729_CAGE Questions_result', 
    value: u, 
    value_text: 'points', 
    message: msg 
});
