data.logic:
var calc_output = []; 
var one = parseFloat(one) || 0;
var two = parseFloat(two) || 0;
var three = parseFloat(three) || 0;
var four = parseFloat(four) || 0;
var five = parseFloat(five) || 0;
var articulardisease = parseFloat(articulardisease);

var score, msg, longmsg;

if (articulardisease == 0) {
  score = '';
  msg = 'Not PsA';
  longmsg = 'Not PsA by CASPAR Criteria; required criterion not met';
} else {
  score = one + two + three + four + five; 
  if (score >= 3) {
    msg = 'Positive';
    longmsg = 'Diagnosis is positive by CASPAR Criteria';
  } else {
    msg = 'Negative';
    longmsg = 'Diagnosis is negative by CASPAR Criteria';
  }
}

//results
calc_output.push({
    name: 'mini',
    value: msg,
    value_text: '',
    message: longmsg
});
if (articulardisease == 1) {
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: 'Total score'   
});
}
calc_output.push({
    name: 'risk1',
    value: msg, 
    value_text: '',
    message: longmsg
});
