data.logic:
var calc_output = [];
var path = 0;
var major = 0;
var minor = 0;
var msg = '';
var value_txt;
path = parseFloat(microorganisms) + parseFloat(lesions);
major = parseFloat(endocarditis) + parseFloat(evidence_endocardial);
minor = parseFloat(heart_condition_injection_drug) + parseFloat(fever) + parseFloat(vascular) + parseFloat(immunologic) + parseFloat(microbiological);
if (path > 0) { 
	msg += 'This patient meets pathologic criteria for definite infective endocarditis.';
    value_txt = 'Definite' 
} else if ((major > 1) || (major > 0 && minor > 2) || (minor > 4)) { 
	msg += 'This patient meets clinical criteria for definite infective endocarditis.';
    value_txt = 'Definite' 
} else if ((major > 0 && minor > 0) || (minor > 2)) { 
	msg += 'This patient meets clinical criteria for possible infective endocarditis.';
    value_txt = 'Possible' 
} else { 
	msg += 'Endocarditis is typically rejected as a diagnosis when: (1) Firm alternate diagnosis explaining evidence of infective endocarditis; (2) Resolution of infective endocarditis syndrome with antibiotic therapy for \u22654 days; (3) No pathologic evidence of infective endocarditis at surgery or autopsy, with antibiotic therapy for \u22644 days; (4) Does not meet criteria for possible infective endocarditis, as above.';
    value_txt = 'Rejected' }

calc_output.push({ 
	name: 'mini', 
	value: value_txt, 
	value_text: '', 
	message: 'See Next Steps for full criteria.' 
});
calc_output.push({ 
	name: '1731_Duke Criteria for IE_result', 
	value: value_txt, 
	value_text: '', 
	message: 'See Next Steps for full criteria.'
});
