var calc_output=[]; var msg=''; var mini_msg=''; var u=parseFloat(concordant_st_elevation)+parseFloat(concordant_st_depression)+parseFloat(discordant_st_elevation);if(u<3){mini_msg='Low specificity'; msg='In the original Sgarbossa criteria, a score of <3 typically is not considered diagnostic of acute MI, but also does not rule out MI. In concerning patients, repeating EKGs and cardiac enzymes may be helpful, along with cardiology consultation.<br/><br/>NOTE: the Modified Sgarbossa Criteria (which changes the third criteria) does not use the points system, it is positive if any criteria are met.'}if(u>2){mini_msg='High specificity'; msg='A score of \u2265 3 has a specificity of 90% for diagnosing acute MI.<br/><br/>Also note: the Modified Sgarbossa Criteria (which changes the third criteria) does not use the points system, it is positive if any criteria are met.'} 

calc_output.push({
    name:'mini',
    value:u,
    value_text:'points',
    message:mini_msg
}); 
calc_output.push({
    name:"1732_Sgarbossa's Criteria for MI_result",
    value:u,
    value_text:'points',
    message:msg
});
