data.logic:
var calc_output = [];

var dialysis = parseFloat(dialysis);

var inr = (parseFloat(inr) < 1) ? 1 : parseFloat(inr); // does not need conversion
var na = parseFloat(sodium); // does not need conversion



if (UOMSYSTEM == true) {
  var bili = parseFloat(bilirubin);
  var cr = parseFloat(creatinine);

  if (bili < 1) {
    bili = 1;
  }
  if (cr < 1) {
    cr = 1;
  }

}

if (UOMSYSTEM == false) {
  var bili = parseFloat(bilirubin) / 17.104;
  var cr = parseFloat(creatinine) / 88.4;

  // these are now converted to US units, so now do a check to make sure they're not <1, otherwise set to 1

  if (bili < 1) {
    bili = 1;
  }
  if (cr < 1) {
    cr = 1;
  }

}



cr = (dialysis == 1 || cr > 4) ? 4 : cr; // if you're on dilaysis or cr>4 set cr=4


if (na < 125) {
  na = 125;
}
if (na > 140) {
  na = 140;
}



var value = 10 * ((.378 * Math.log(bili)) + (1.12 * Math.log(inr)) + (.957 *
  Math.log(cr)) + .643);
var meldna = value - na - (0.025 * value * (140 - na)) + 140;
var mort;

if (meldna < 17) {
  mort = '<2%';
}
if (meldna >= 17 && meldna <= 20) {
  mort = '3 - 4%';
}
if (meldna > 20 && meldna < 23) {
  mort = '7 - 10%';
}
if (meldna >= 23 && meldna <= 26) {
  mort = '14 - 15%';
}
if (meldna >= 27 && meldna <= 31) {
  mort = '27 - 32%';
}
if (meldna >= 32) {
  mort = '65 - 66%';
}
calc_output.push({
  name: 'mini',
  value: meldna.toFixed(0),
  value_text: 'points',
  message: 'MELD-Na Score'
});
calc_output.push({
  name: '1754_MELD-Na Score_result (score)',
  value: meldna.toFixed(0),
  value_text: 'points',
  message: 'MELD-Na Score'
});
calc_output.push({
  name: '1754_MELD-Na Score_result (mortality)',
  value: mort,
  value_text: ' ',
  message: 'Estimated 90-Day Mortality'
});
