data.logic:
var calc_output = [];
var msg, mini_msg, percent;
var output_score = parseInt(thrombocytopenia) + parseInt(platelet_fall_timing) + parseInt(thrombosis) + parseInt(other_causes_thrombocytopenia);
if (output_score < 4) {
    mini_msg = 'Low Probability of HIT';
    percent = '<5%';
    msg = 'Low Probability of HIT (<5%)'
}
if (output_score == 4 || output_score == 5) {
    mini_msg = 'Intermediate Probability of HIT';
    percent = '~14%';
    msg = 'Intermediate Probability of HIT (~14%)'
}
if (output_score > 5) {
    mini_msg = 'High Probability of HIT';
    percent = '~64%';
    msg = 'High Probability of HIT (~64%)'
}
calc_output.push({
    name: 'mini',
    value: percent,
    value_text: ' ',
    message: mini_msg
});
calc_output.push({
    name: '1787_4Ts for HIT_result',
    value: output_score,
    value_text: 'points',
    message: msg
});
