data.logic:
var calc_output = [];
var result = 'High'
var rec, mini

if (ill == 0) {
	if (age == 0) {
		if (leuk == 0) {
			if (pct == 0) {
				if (crp == 0) {
					result = 'Low'
				}
				else if (crp == 1) {
					result = 'Intermediate'
				}
			}
		}
	}
}

if (result == 'Low') {
	rec = 'Risk of invasive bacterial infection 0.7%. Full sepsis workup likely not necessary.'
	mini = 'Limited workup'
}
if (result == 'High') {
	rec = 'Risk of invasive bacterial infection 8.1%. Full sepsis workup indicated (follow local practices, but generally includes pan-culture, empiric antibiotics, chest x-ray, and admission).'
	mini = 'Full workup'
}
if (result == 'Intermediate') {
	rec = 'Risk of invasive bacterial infection 3.4%. Full sepsis workup, including blood, urine, and CSF cultures, broad spectrum IV antibiotics, and admission MAY be indicated, especially if patient is 21-28 days old.'
	mini = 'May need full workup'
}

//results
calc_output.push({
    name: 'mini',
    value: result,
    value_text: 'risk',
    message: ''
});
calc_output.push({
    name: '1801_Step-by-Step Approach to Fever_result',
    value: result,
    value_text: 'risk',
    message: rec
});
