var calc_output = [];

var toxicity = parseFloat(toxicity) 
var comorbidities = parseFloat(comorbidities)

var grade
var treatment

if (toxicity == 1) {
	grade = 'Grade 1 CRS'
	treatment = 'Vigilant supportive care, assess for infection (treat fever and neutropenia if present; monitor fluid balance; give antipyretics/analgesics as needed)'
}
else if (toxicity == 2 && comorbidities == 0){
	grade = 'Grade 2 CRS'
	treatment = 'Vigilant supportive care (monitor cardiac and other organ function closely)'
}
else if (toxicity == 2 && comorbidities == 1){
	grade = 'Grade 2 CRS'
	treatment = 'Vigilant supportive care, tocilizumab ± corticosteroids'
}
else if (toxicity == 3 || toxicity == 4){
	grade = 'Grade 3-4 CRS'
	treatment = 'Vigilant supportive care, tocilizumab ± corticosteroids'
}
else if (toxicity == 5){
	grade = 'Grade 5 CRS'
    treatment = '(Death)'
}

//results
calc_output.push({
    name: 'mini',
    value: grade,
    value_text: '',
    message: treatment 
    });
