data.logic:
var calc_output = [];
var u = parseInt(weight_bearing_inability) + parseInt(temperature) + parseInt(esr_40) + parseInt(white_blood_cell_count);
var msg = '';
var mini_msg = '';
if (u == 0) {
    msg = 'If no criteria are positive, this child is at very low risk for septic arthritis in the appropriate clinical setting and may be managed with observation only and close follow-up.';
    mini_msg = 'Very low risk for septic arthritis.'
}
if (u == 1) {
    msg = '3% probability of septic arthritis; intermediate probability and may require radiology or orthopedics consultation for hip aspiration.';
    mini_msg = '3% probability of septic arthritis.'
}
if (u == 2) {
    msg = '40% probability of septic arthritis; intermediate probability and may require radiology or orthopedics consultation for hip aspiration.';
    mini_msg = '40% probability of septic arthritis.'
}
if (u == 3) {
    msg = '93% probability of septic arthritis; may be a candidate for aspiration in the operating room instead of the radiology suite given greater likelihood that they will require surgical drainage.';
    mini_msg = '93% probability of septic arthritis.'
}
if (u == 4) {
    msg = '99% probability of septic arthritis; may be a candidate for aspiration in the operating room instead of the radiology suite given greater likelihood that they will require surgical drainage.';
    mini_msg = '99% probability of septic arthritis.'
}
calc_output.push({ 
    name: 'mini', 
    value: u, 
    value_text: 'criteria', 
    message: mini_msg 
});
calc_output.push({ 
    name: '1817_Kocher Criteria_result', 
    value: u, 
    value_text: 'criteria', 
    message: msg 
});
