data.logic:
var calc_output = [];
var u = parseFloat(estimated_blood_loss) + parseFloat(lowest_mean_arterial_pressure) + parseFloat(lowest_heart_rate);
var msg = '';
var msgalt = '';
var mini_msg = '';
if (u < 3) {
    msg = 'Very high risk patient of poor outcome. 14% mortality rate, overall 75% risk of major complication including death.';
    msgalt = '<br/><br/>In one follow-up study, patients with scores \u22644 were 97% specific for post-operative ICU admission if not initially admitted there.';
    mini_msg = 'Very high risk'
} else if (u < 5) {
    msg = 'Very high risk patient of poor outcome. 14% mortality rate, overall 56% risk of major complication including death.';
    msgalt = '<br/><br/>In one follow-up study, patients with scores \u22644 were 97% specific for post-operative ICU admission if not initially admitted there.';
    mini_msg = 'Very high risk'
} else if (u < 7) {
    msg = 'High risk patient of poor outcome. 4% mortality rate, overall 16% risk of major complication including death.';
    mini_msg = 'High risk'
} else if (u < 9) {
    msg = 'Moderate risk patient of poor outcome. 1% mortality rate, overall 6% risk of major complication including death.';
    mini_msg = 'Moderate risk'
} else {
    msg = 'Low risk patient of poor outcome. 0% mortality rate, overall <4% risk of major complication including death.';
    mini_msg = 'Low risk'
}
calc_output.push({ 
    name: 'mini', 
    value: u.toFixed(0), 
    value_text: 'points', 
    message: mini_msg 
});
calc_output.push({ 
    name: '1826_Surgical Apgar Score_result', 
    value: u.toFixed(0), 
    value_text: 'points', 
    message: msg + msgalt 
});
