data.logic:
var calc_output=[]; 
var mini_msg=''; 
var msg='';

if(parseFloat(patient_response)==2){mini_msg='Low risk'; msg='A SMS score of 2 correlates with a low risk of need for intubation, neurosurgical intervention or mortality (like a GCS of 14-15).'}else{mini_msg='Increased risk'; msg='Patients with an SMS of <2 are at significantly increased risk of having a significant traumatic brain injury and prompt evaluation and head CT imaging is indicated for these patients.'} 

calc_output.push({
    name:'mini',
    value:parseFloat(patient_response),
    value_text:'points',message:mini_msg
}); 
calc_output.push({
    name:'1832_Simplified Motor Score_result',
    value:parseFloat(patient_response),
    value_text:'points',
    message:msg
});
