data.logic:
var calc_output = [];
var mini_msg = '';
abnormal_ecg = parseFloat(abnormal_ecg);
troponin = parseFloat(troponin);
acs_nitrate_use = parseFloat(acs_nitrate_use);
palpation = parseFloat(palpation);
age_50 = parseFloat(age_50);
pain_radiate = parseFloat(pain_radiate);
var u = 1;
msg = '';
if (abnormal_ecg + troponin + acs_nitrate_use >= 1) {
    mini_msg = 'Standard Evaluation';
    msg = 'This patient is not a candidate for early discharge and should receive a standard chest pain evaluation.'
} else {
    if (palpation == 1) {
        mini_msg = 'Low Risk. Step 3 Not Required.';
        msg = 'If chest pain is reproducible in Step 2, this patient is at low risk for adverse event and is a candidate for early discharge, and does not require Step 3 questions.'
    } else {
        if (age_50 + pain_radiate <= 2) {
            mini_msg = 'Standard evaluation';
            msg = 'This patient is not a candidate for early discharge and should receive a standard chest pain evaluation.'
        }
        if (age_50 + pain_radiate == 0) {
            mini_msg = 'Low risk';
            msg = 'This patient is at low risk for adverse event and is a candidate for early discharge.'
        }
    }
}
calc_output.push({
    name: 'mini',
    value: ' ',
    value_text: ' ',
    message: mini_msg
});
calc_output.push({
    name: '1856_Vancouver Chest Pain Rule_result',
    value: mini_msg,
    value_text: ' ',
    message: msg
});
