data.logic:
var calc_output = []; 

var msg = ''; 

var mini_msg = ''; 

var artery_disease_risk_factors = parseFloat(artery_disease_risk_factors); 
var u = parseFloat(sex)  + parseFloat(diaphoresis) + parseFloat(radiates) + parseFloat(pain_inspiration) + parseFloat(pain_palpation); 
age = parseFloat(age); 

if (age > 17) {u = u + 2; } 
if (age > 45) {u = u + 2; } 
if (age > 50) {u = u + 2; } 
if (age > 55) {u = u + 2; } 
if (age > 60) {u = u + 2; } 
if (age > 65) {u = u + 2; }
if (age > 70) {u = u + 2; } 
if (age > 75) {u = u + 2; } 
if (age > 80) {u = u + 2; } 
if (age > 85) {u = u + 2; } 

if (age <=50) {
    u = u + artery_disease_risk_factors;
}

if (u < 16) {
    msg = 'Low risk by the EDACS Score.<br/><br/>If the patient also has: (1) EKG without new ischemic changes and (2) negative initial and 2-hour troponins, then this patient is safe for discharge to early outpatient follow-up investigation (or proceed to earlier inpatient testing). If EKG with ischemic changes or positive troponin, they are not low risk and require normal risk stratification.'; 
    mini_msg = 'Low Risk.'; 
} else {
    msg = 'Not low risk. This patient is not a candidate for early discharge and should receive a standard chest pain evaluation with delayed troponin testing.'; 
    mini_msg = 'Not Low Risk.'; 
} 

calc_output.push({
    name: 'mini', 
    value: u.toFixed(0), 
    value_text: 'points', 
    message: mini_msg 
}); 
calc_output.push({
    name: '1858_EDACS_result', 
    value: u.toFixed(0), 
    value_text: 'points', 
    message: msg 
});
