data.logic:
var calc_output = [];
var msg, msgalt, mini_v;
if (rass_3 == 0) { 
    mini_v = 'Too sedated';
    msg = 'Patient is too sedated';
    msgalt = 'Complete CAM-ICU at a later time.' 
} else if (rass_3 == 1) {
    if (baseline_status == 0 && mental_status == 0) { 
        mini_v = 'Negative';
        msg = 'CAM-ICU negative';
        msgalt = 'Delirium absent.' 
    } else if (baseline_status == 1 || mental_status == 1) {
        if (attention_test == 1) {
            if (rass_score == 0 && error_questions == 0) { 
                mini_v = 'Negative';
                msg = 'CAM-ICU negative';
                msgalt = 'Delirium absent.' 
            } else if (rass_score == 1 || error_questions == 1) {
                mini_v = 'Positive';
                msg = 'CAM-ICU positive';
                msgalt = 'Delirium present.' 
            } 
        } 
        else {
            mini_v = 'Negative';
            msg = 'CAM-ICU negative';
            msgalt = 'Delirium absent.' 
        }
    } 
}
else {
        mini_v = 'Negative';
        msg = 'CAM-ICU negative';
        msgalt = 'Delirium absent.' 
    } 
calc_output.push({ 
    name: 'mini', 
    value: mini_v, 
    value_text: ' ', 
    message: 'CAM-ICU' 
});
calc_output.push({ 
    name: '1870_CAM-ICU_result', 
    value: msg, 
    value_text: ' ', 
    message: msgalt 
});
