data.logic:
var calc_output=[]; 
var u=parseFloat(patient_description); 
var msg='';
var mini_msg=''; 
if(u<=-3){
    msg='Patients with a RASS of \u2264-3 should have their sedation decreased or modified in order to achieve a RASS of -2 to 0.';
    mini_msg='Sedatition should be decreased or modified.'
} 
if(u==2||u==3||u==4){
    msg='Patients with a RASS of 2 to 4 are not sedated enough and should be assessed for pain, anxiety, or delirium. The underlying etiology of the agitation should be investigated and appropriately treated to achieve a RASS of -2 to 0.';
    mini_msg='Sedation not sufficient. Assess for pain, anxiety, delirium.'
} 
if(u==-2||u==-1||u==0||u==1){
    msg='Patients with a RASS of -2 to 1 are properly sedated.';
    mini_msg='Proper sedation.'
} 

calc_output.push({
    name:'mini',
    value:u,
    value_text:'points',
    message:mini_msg
}); 
calc_output.push({
    name:'1872_RASS_result',
    value:u,
    value_text:'points',
    message:msg
});
