data.logic:
var aspiration_signs = aspiration_signs || 0

var calc_output = [];
var msg = '';
var mini_msg = '';
var glasgow_coma_scale_13 = parseFloat(glasgow_coma_scale_13);
var facial = parseFloat(facial);
var tongue = parseFloat(tongue);
var palatal = parseFloat(palatal);

aspiration_signs = parseFloat(aspiration_signs);

if (glasgow_coma_scale_13 == 1 || facial == 1 || tongue == 1 || palatal == 1) {
    msg = 'If any of the four screening questions are \u2018Yes,\u2019 refer to speech pathology; do not perform water test.';
    mini_msg = 'Do not perform water test.';
} else {
    if (aspiration_signs == 1) {
        msg = 'Failure of the water swallow screen. Refer to speech pathology prior to diet.';
        mini_msg = 'Refer to speech pathology prior to diet.';
    } else {
        msg = 'Swallow screen passed. Low risk of aspiration with regular diet.';
        mini_msg = 'Low risk.';
    }
}
calc_output.push({
    name: 'mini',
    value: ' ',
    value_text: ' ',
    message: mini_msg
});
calc_output.push({
    name: '1894_Barnes Jewish Hospital Stroke Dysphagia Screen_result',
    value: '',
    value_text: '',
    message: msg+'<br/><br/>The BJH-SDS is >90% sensitive for both dysphagia and aspiration.'
});
