data.logic:
var calc_output = [];

var v = 10 * parseFloat(white_blood_cell_count) * (parseFloat(bands) +
  parseFloat(neutrophils));
var msg;

if (v < 500) {
  msg = 'Severe';
}
if (v >= 500 && v < 1000) {
  msg = 'Moderate';
}
if (v >= 1000 && v < 1500) {
  msg = 'Mild';
}
if (v >= 1500 && v <= 7700) {
  msg = 'Normal; no'
}
if (v > 7700) {
  msg = 'Neutrophilia'
}

if (v <= 7700) {
  calc_output.push({
    name: 'mini',
    value: v,
    value_text: 'cells/µL',
    message: msg + ' neutropenia'
  });
} else {
  calc_output.push({
    name: 'mini',
    value: v,
    value_text: 'cells/µL',
    message: 'High ANC'
  });
}
calc_output.push({
  name: '19_Absolute Neutrophil Count_result',
  value: v,
  value_text: 'cells/µL',
  message: 'Absolute Neutrophil Count'
});
if (v < 1500) {
calc_output.push({
  name: '19_Absolute Neutrophil Count_interpretation',
  value: msg,
  value_text: 'neutropenia',
  message: ''
});
} else if (v <= 7700) {
  calc_output.push({
  name: '19_Absolute Neutrophil Count_interpretation',
  value: 'Normal',
  value_text: '',
  message: 'No neutropenia'
  });
} else {
  calc_output.push({
    name: '19_Absolute Neutrophil Count_interpretation',
    value: 'High ANC',
    value_text: '',
    message: msg
  });
}
