data.logic:
var calc_output=[]; 

var mini_msg=''; 
var u=1;msgadd='';
if((parseFloat(temperature)+parseFloat(tachycardia)+parseFloat(tachypnea)+parseFloat(leukocyte)>=2)&&(parseFloat(temperature)+parseFloat(leukocyte)>0)){mini_msg='Meets SIRS criteria'; msg='This patient meets SIRS criteria.';if(parseFloat(infection)==1){mini_msg='Meets sepsis criteria'; msg='This patient meets sepsis criteria.<br/><br/>';msgadd=' Screen for Severe Sepsis and Septic Shock physical exam findings. If present, initiate ACCM/PALS Septic Shock Algorithm (see Next Steps section).<br/><br/>Consider IV fluid resuscitation, cultures, and broad-spectrum antibiotics.'}if(parseFloat(severe_sepsis)==1){mini_msg='Severe sepsis'; msg='This patient meets severe sepsis criteria.<br/><br/>'}if(parseFloat(septic_shock)==1){mini_msg='Septic shock'; msg='This patient meets septic shock criteria.<br/><br/>'}}else{mini_msg='Does not meet SIRS'; msg='This patient does not meet SIRS criteria. <br/><br/> For other causes of shock, see the Next Steps section.'}msg+=msgadd; 

calc_output.push({
    name:'mini',
    value:' ',
    value_text:' ',
    message:mini_msg
}); 
