data.logic:
var calc_output = []; var msg = ''; var msgAppend = ''; var consider = ''; var u = parseFloat(ddimer) + parseFloat(age_50) + parseFloat(male) + parseFloat(hormone); if (u == -2) {msg = '1.8% annual risk of VTE recurrence.'; consider = '<br/><br/>Consider discontinuing anticoagulation.'; msgAppend = '<br/><br/>NOTE: A very small group (2.3% of studied sample) had DASH Score of -2 in the derivation study.'; } else if (u == -1) {msg = '1.0% annual risk of VTE recurrence.'; consider = '<br/><br/>Consider discontinuing anticoagulation.'; } else if (u == 0) {msg = '2.4% annual risk of VTE recurrence.'; consider = '<br/><br/>Consider discontinuing anticoagulation.'; } else if (u == 1) {msg = '3.9% annual risk of VTE recurrence.'; consider = '<br/><br/>Consider discontinuing anticoagulation.'; } else if (u == 2) {msg = '6.3% annual risk of VTE recurrence.'; consider = '<br/><br/>Consider continuing anticoagulation.'; } else if (u == 3) {msg = '10.8% annual risk of VTE recurrence.'; consider = '<br/><br/>Consider continuing anticoagulation.'; } else if (u == 4) {msg = '19.9% annual risk of VTE recurrence.'; consider = '<br/><br/>Consider continuing anticoagulation.'; } 

calc_output.push({
    name: 'mini', 
    value: u.toFixed(0), 
    value_text: 'points',
    message: msg 
}); 
calc_output.push({
    name: '2011_DASH Score_result', 
    value: u.toFixed(0), 
    value_text: 'points', 
    message: msg + consider + msgAppend 
});
