data.logic:
var calc_output = []; 
var msg = ''; 
var mini_msg = ''; 
var msgalt = ''; 
var u = parseFloat(often) + parseFloat(count) + parseFloat(more); 
if (u < 3) {msg = 'Scores <3 are consistent with normal alcohol consumption.'; mini_msg = 'Normal alcohol consumption.'; } 
if (u == 3) {msg = 'For females: Scores \u22653 are consistent with alcohol misuse. For males: Normal alcohol consumption.'; mini_msg = 'Alcohol misuse for females only.'; } 
if (u == 4) {msg = 'For females: Scores \u22653 are consistent with alcohol misuse. For males: Scores \u22654 are consistent with alcohol misuse.'; mini_msg = 'Alcohol misuse.'; } 
else if (u > 4) {msg = 'Scores \u22655 are consistent with alcohol misuse and possible liver damage.'; mini_msg = 'Alcohol misuse and possible liver damage.'; } 
if (count + more == 0 && often > 0) {msgalt = 'When points are only from Question 1, patients are often drinking below the AUDIT-C cutoff; to confirm accuracy, review patient\u2019s alcohol intake over past months.'; } 

calc_output.push({
    name: 'mini', 
    value: u, 
    value_text: 'points', 
    message: mini_msg 
}); 
calc_output.push({
    name: '2021_AUDIT-C_result', 
    value: u, 
    value_text: 'points', 
    message: msg+'<br/>'+msgalt 
});
