data.logic:
var calc_output = [];
var msg = '';
var mini_msg = '';

var age = parseFloat(age); // doesn't change
var pt = parseFloat(pt); // doesn't change
var cr = parseFloat(cr);
// usually we use US units. Lille uses SI units. Need to convert to SI units -- Lille est né en france!

if (UOMSYSTEM == true) {
  var alb = parseFloat(alb) * 10;
  var bili7 = parseFloat(bili7) * 17.104;
  var bili_init = parseFloat(bili_init) * 17.104;

  // cr isn't actually the cr value, it's "renal insufficiency" which is a Cr >1.3 mg/dL
  if (parseFloat(cr) > 1.3) {
    var cr = 1;
  } else {
    var cr = 0;
  }
}

if (UOMSYSTEM == false) {
  var alb = parseFloat(alb);
  var bili7 = parseFloat(bili7);
  var bili_init = parseFloat(bili_init);

  // cr isn't actually the cr value, it's "renal insufficiency" which is a Cr >115 µmol/L
  if (parseFloat(cr) > 115) {
    var cr = 1;
  } else {
    var cr = 0;
  }
}

var r = 3.19 - (0.101 * age) + (0.147 * alb) + (0.0165 * (bili_init - bili7)) -
  (0.206 * cr) - (0.0065 * bili_init) - (0.0096 * pt);
var u = Math.exp(-r) / (Math.exp(-r) + 1);

// Point system. No need to unconvert.
if (u > 0.45) {
  var msg =
    'Poor prognosis. The patient may be a non-responder to steroids. Consider alternative therapies and/or referral for transplant.';
  mini_msg = 'Poor prognosis.';
} else if (u < 0.45) {
  var msg = 'Good prognosis. Scores <0.45 predict a 6-month survival of 85%.';
  mini_msg = 'Good prognosis.';
}
calc_output.push({
  name: 'mini',
  value: u.toFixed(3),
  value_text: 'points',
  message: mini_msg
});
calc_output.push({
  name: '2024_Lille Model_result',
  value: u.toFixed(3),
  value_text: 'points',
  message: msg
});
