data.logic:
var calc_output=[]; 
var score=parseFloat(hist)+parseFloat(nihss_score)+parseFloat(hypodensity); 
var anyHemRisk,fatalHemRisk; 

if(score==0){anyHemRisk='2';fatalHemRisk='0'} 
else if(score==1){anyHemRisk='5';fatalHemRisk='3'} 
else if(score==2){anyHemRisk='10';fatalHemRisk='7'} 
else if(score==3){anyHemRisk='15';fatalHemRisk='6'} 
else if(score>3){anyHemRisk='44';fatalHemRisk='33'} 

calc_output.push({
    name:'mini',
    value:score.toFixed(0),
    value_text:'points',
    message:'HAT Score'
}); 
calc_output.push({
    name:'2030_HAT Score_result (score)',
    value:score.toFixed(0),
    value_text:'points',
    message:'HAT Score'
}); 
calc_output.push({
    name:'2030_HAT Score_result (symptomatic)',
    value:anyHemRisk,
    value_text:'%',
    message:'Risk of Symptomatic ICH.'
});
calc_output.push({
    name:'2030_HAT Score_result (fatal)',
    value:fatalHemRisk,
    value_text:'%',
    message:'Risk of Fatal ICH.'
});
