data.logic:
var calc_output = [];
var u = parseFloat(gluc) + parseFloat(infarct) + parseFloat(ct) + parseFloat(age) + parseFloat(nihss);
var msg = '';
var mini_msg = '';

if (u == 0) {
    msg = '1.6% risk of symptomatic ICH. (ECASS-II definition.)';
    mini_msg = '1.6% risk of symptomatic ICH.'
} else if (u == 1) {
    msg = '3.3% risk of symptomatic ICH. (ECASS-II definition.)';
    mini_msg = '3.3% risk of symptomatic ICH.'
} else if (u == 2) {
    msg = '5.4% risk of symptomatic ICH. (ECASS-II definition.)';
    mini_msg = '5.4% risk of symptomatic ICH.'
} else if (u == 3) {
    msg = '8.8% risk of symptomatic ICH. (ECASS-II definition.)';
    mini_msg = '8.8% risk of symptomatic ICH.'
} else if (u == 4) {
    msg = '12.3% risk of symptomatic ICH. (ECASS-II definition.)';
    mini_msg = '12.3% risk of symptomatic ICH.'
} else if (u >= 5) {
    msg = '16.9% risk of symptomatic ICH. (ECASS-II definition.)';
    mini_msg = '16.9% risk of symptomatic ICH.'
}
calc_output.push({
    name: 'mini',
    value: u,
    value_text: 'points',
    message: mini_msg
});
calc_output.push({
    name: '2039_Sedan Score_result',
    value: u,
    value_text: 'points',
    message: msg
});
