data.logic:
var calc_output = [];

if (UOMSYSTEM == true) {
	weight = weight * 28.3495;
}
else {
  weight = weight;
}

hct_initial = parseFloat(hct_initial) / 100;
hct_goal = parseFloat(hct_goal) / 100;
hct_trans = parseFloat(hct_trans) / 100;

var feto_vol = weight * 0.14;
var trans_vol = feto_vol * (hct_goal - hct_initial) / hct_trans;

if (trans_vol <= 0) {
	calc_output.push({
	   name: 'mini',
	   value: '',
	   value_text: '',
	   message: 'Goal Hct should be greater than initial Hct'
	});
	calc_output.push({
	   name: '10004_Intrauterine RBC Transfusion Dosage_result',
	   value: '',
	   value_text: '',
	   message: 'Goal Hct should be greater than initial Hct'
	});
}
else {
calc_output.push({
   name: 'mini',
   value: trans_vol.toFixed(1),
   value_text: 'mL',
   message: 'RBC volume for transfusion'
});
calc_output.push({
   name: '10004_Intrauterine RBC Transfusion Dosage_result',
   value: trans_vol.toFixed(1),
   value_text: 'mL',
   message: 'RBC volume for transfusion'
});
}
