data.logic:
var calc_output = [];
var msg;
var equivin;
var equivout;
var ihalf;
var ohalf;
var adjust = "";
var druginputname = "";
var drugoutputname = "";
var drugmg = parseFloat(drug_dosage);
var drug_input = parseFloat(drug_input);
var drug_output = parseFloat(drug_output);
if (drug_input == 1) {
    equivin = 0.75;
    ihalf = 3;
    druginputname = "Betamethasone";
} else if (drug_input == 2) {
    equivin = 25;
    ihalf = 1;
    druginputname = "Cortisone";
} else if (drug_input == 3) {
    equivin = 0.75;
    ihalf = 3;
    druginputname = "Dexamethasone (Decadron)";
} else if (drug_input == 4) {
    equivin = 20;
    ihalf = 1;
    druginputname = "Hydrocortisone";
} else if (drug_input == 5) {
    equivin = 4;
    ihalf = 2;
    druginputname = "Methylprednisolone (Solu-Medrol)";
} else if (drug_input == 6) {
    equivin = 5;
    ihalf = 2;
    druginputname = "Prednisolone";
} else if (drug_input == 7) {
    equivin = 5;
    ihalf = 2;
    druginputname = "Prednisone";
} else if (drug_input == 8) {
    equivin = 4;
    ihalf = 2;
    druginputname = "Triamcinolone";
} else {
    equivin = 0;
    ihalf = 0;
    druginputname = "N/A";
}
if (drug_output == 1) {
    equivout = 0.75;
    ohalf = 3;
    drugoutputname = "Betamethasone";
} else if (drug_output == 2) {
    equivout = 25;
    ohalf = 1;
    drugoutputname = "Cortisone";
} else if (drug_output == 3) {
    equivout = 0.75;
    ohalf = 3;
    drugoutputname = "Dexamethasone (Decadron)";
} else if (drug_output == 4) {
    equivout = 20;
    ohalf = 1;
    drugoutputname = "Hydrocortisone";
} else if (drug_output == 5) {
    equivout = 4;
    ohalf = 2;
    drugoutputname = "Methylprednisolone (Solu-Medrol)";
} else if (drug_output == 6) {
    equivout = 5;
    ohalf = 2;
    drugoutputname = "Prednisolone";
} else if (drug_output == 7) {
    equivout = 5;
    ohalf = 2;
    drugoutputname = "Prednisone";
} else if (drug_output == 8) {
    equivout = 4;
    ohalf = 2;
    drugoutputname = "Triamcinolone";
} else {
    equivin = 0;
    ihalf = 0;
    druginputname = "N/A";
}
var u = drugmg * equivout / equivin;
if (ihalf > ohalf) { adjust = "<br/><br/>You are converting to a drug with a shorter half-life, consider increasing dosing frequency."; }
if (ihalf < ohalf) { adjust = "<br/><br/>You are converting to a drug with a longer half-life, consider reducing dosing frequency."; }
msg = "Approximate <strong>" + drugoutputname + "</strong> Equivalence to:<br/><br/>" + drugmg + "mg of " + druginputname + adjust;

if(drug_input == drug_output){
  calc_output.push({ 
      name: "mini", 
      value: 'N/A', 
      value_text: "", 
      message: 'Conversion not applicable if converting between the same steroid' 
    });
  calc_output.push({ 
      name: "2040_Steroid Conversion_result", 
      value: 'N/A', 
      value_text: "", 
      message: 'Conversion not applicable if converting between the same steroid' 
    });
} else {
  calc_output.push({ 
      name: "mini", 
      value: u.toFixed(1), 
      value_text: "mg", 
      message: drugoutputname 
    });
  calc_output.push({ 
      name: "2040_Steroid Conversion_result", 
      value: u.toFixed(1), 
      value_text: "mg", 
      message: msg 
    });
}
