data.logic:
var calc_output=[]; 
var u=parseInt(size)+parseInt(tract)+parseInt(obstruction)+parseInt(calyces)+parseInt(essence); 
var msg='';
var mini_msg=''; 
var percent; 
if(u==1||u==2||u==3||u==4){percent='100%';msg='100% chance of patient being stone free after first procedure.';mini_msg='Probability of Stone Free'} 
else if(u==5){percent='94%';msg='94% chance of patient being stone free after first procedure.';mini_msg='Probability of Stone Free'} 
else if(u==6){percent='88%';msg='88% chance of patient being stone free after first procedure.';mini_msg='Probability of Stone Free'} 
else if(u==7){percent='92%';msg='92% chance of patient being stone free after first procedure.';mini_msg='Probability of Stone Free'} 
else if(u==8){percent='83%';msg='83% chance of patient being stone free after first procedure.';mini_msg='Probability of Stone Free'} 
else if(u==9){percent='64%';msg='64% chance of patient being stone free after first procedure.';mini_msg='Probability of Stone Free'} 
else if(u==10){percent='42%';msg='42% chance of patient being stone free after first procedure.';mini_msg='Probability of Stone Free'} 
else if(u>=11){percent='27%';msg='27% chance of patient being stone free after first procedure.';mini_msg='Probability of Stone Free'} 

calc_output.push({
    name:'mini',
    value:percent,
    value_text:' ',
    message:mini_msg
}); 
calc_output.push({
    name:'2044_STONE Score (Post-CT, Renal)_result',
    value:u.toFixed(0),
    value_text:'points',
    message:msg
});
