data.logic:
var calc_output = [];
var msg = '';
var mini_msg = '';
var grade = '';
var v = parseInt(age) + parseInt(psa) + parseInt(gleason) + parseInt(stage) + parseInt(biopsy);
if (v < 3) { msg = 'Low risk by UCSF-CAPRA.';
    mini_msg = 'Low risk' }
if (v > 2 && v < 6) { msg = 'Intermediate risk by UCSF-CAPRA.';
    mini_msg = 'Intermediate risk' }
if (v > 5) { msg = 'High risk by UCSF-CAPRA.';
    mini_msg = 'High risk' }
if (v == 0 || v == 1) { grade = '~85% disease-free survival at 5 years.'; }
if (v == 2) { grade = '75-81% disease-free survival at 5 years.'; }
if (v == 3) { grade = '~66% disease-free survival at 5 years.'; }
if (v == 4) { grade = '~61% disease-free survival at 5 years.'; }
if (v == 5) { grade = '52-60% disease-free survival at 5 years.'; }
if (v == 6) { grade = '29-34% disease-free survival at 5 years.'; }
if (v > 6) { grade = '8-20% disease-free survival at 5 years.'; }
calc_output.push({ 
    name: 'mini', 
    value: v, 
    value_text: 'points', 
    message: mini_msg 
});
calc_output.push({ 
    name: '2046_UCSF-CAPRA Score_result', 
    value: v, 
    value_text: 'points', 
    message: msg + ' ' + grade 
});
