data.logic:
var calc_output=[]; 
var p=parseFloat(primary); 
var s=parseFloat(secondary); 
var t=parseFloat(tertiary); 
var v=p+s; 
var val=p + '+' + s + '=' + v; 
var grade=''; 
var mini_grade=''; 

if(v<6){
    grade='Prognostic grade group I - Unlikely severe cancer is present. Consider close monitoring.';
    mini_grade='Prognostic grade group I'
} 
if(v==6){
    grade='Prognostic grade group I - 97.1 and 98.8% remission of tumor within 2 years of diagnosis.';
    mini_grade='Prognostic grade group I'
} 
if(v>6 && p==3 && s==4){
    grade='Prognostic grade group II - 90.6 and 93.6% remission of tumor within 2 years of diagnosis.';
    mini_grade='Prognostic grade group II'
} 
if(v>6 && p==4 && s==3){
    grade='Prognostic grade group III -  < 90.6% remission of tumor within 2 years of diagnosis.';
    mini_grade='Prognostic grade group III'
}
if(v==8){
    grade='Prognostic grade group IV - 70.9 - 73.7% remission of tumor within 2 years of diagnosis.';
    mini_grade='Prognostic grade group IV'
} 
if(v>8){
    grade='Prognostic grade group V -  66.7 - 58.5% remission of tumor within 2 years of diagnosis.';
    mini_grade='Prognostic grade group V'
} 

calc_output.push({
    name:'mini',
    value:v,
    value_text:'Gleason Score',
    message:mini_grade
}); 
calc_output.push({
    name:'2048_Gleason Score_result',
    value:val,
    value_text:'Gleason Score, Tertiary Grade: '+ t,
    message: grade
});
