data.logic:
var calc_output = [];
var u = parseFloat(ldh) + parseFloat(na) + parseFloat(alkPhos) + parseFloat(bicarb) + parseFloat(stage) + parseFloat(karnofsky);
var prog = '';
if (u == 0 || u == 1) {prog = '16.2%';}
    if (u == 2 || u == 3) {prog = '2.5%';}
        if (u >= 4) {prog = '0%';}
var msg = 'Two-year survival: ~' + prog;

calc_output.push({
    name: 'mini',
    value: u.toFixed(0),
    value_text: 'points',
    message: msg
});
calc_output.push({
    name: '2142_Manchester Score_result',
    value: u.toFixed(0),
    value_text: 'points',
    message: msg
});
