data.logic:
var calc_output = [];
var age = parseFloat(age);
var spl = parseFloat(spl);
var plt = parseFloat(plt);
var blasts = parseFloat(blasts);
var two = '';
var median = '';
var risk = '';

if (UOMSYSTEM == false) {
  spl = spl*2.54;
}

var score = Math.exp((0.0116 * (age - 43.4)) + (0.0345 * (spl - 7.51)) + (0.188 * (Math.pow((plt / 700), 2) - 0.563)) + (0.0887 * (blasts - 2.10)));


if (score.toFixed(1) < 0.80) {
    two = 'Two year survival: 90%';
    median = 'Median survival: 5 years';
    risk = 'Low'
}
if (score.toFixed(1) >= 0.80 && score.toFixed(1) <= 1.2) {
    two = 'Two year survival: 65-90%';
    median = 'Median survival: 2.5-5 years';
    risk = 'Intermediate'
}
if (score.toFixed(1) > 1.2) {
    two = 'Two year survival: 65%';
    median = 'Median survival: 2.5 years';
    risk = 'High'
}


calc_output.push({
    name: 'mini',
    value: score.toFixed(1),
    value_text: 'points',
    message: median
});
calc_output.push({
    name: '2143_Sokal Score_result',
    value: score.toFixed(1),
    value_text: 'points',
    message: 'Sokal Index'
});
calc_output.push({
    name: '2143_Sokal Score_interpretation',
    value: risk,
    value_text: ' risk',
    message: two + '<br/>' + median
});
