data.logic:
var calc_output = [];

var ambulation = parseFloat(ambulation);
var pyramidal = parseFloat(pyramidal);
var cerebellar = parseFloat(cerebellar);
var brainstem = parseFloat(brainstem);
var sensory = parseFloat(sensory);
var bowelAndBladder = parseFloat(bowelAndBladder);
var visual_input = parseFloat(visual_input);
var cerebral = parseFloat(cerebral);
var other = parseFloat(other);


var fs = [pyramidal, cerebellar, brainstem, sensory, bowelAndBladder, visual_input, cerebral, other];

var fsScore = 0;
var uncertain = false;

var g = [];

for (var i = 0; i < 7; i++) {
    g[i] = fs.filter(function(val) {
        return i == val;
    }).length;
}

function compare(a, b) {
    for (var i = 0; i < a.length; i++) {
        var toEval = "(" + a[i] + b[i] + ")";
        if (!(eval(toEval))) {
            return false;
        }
    }
    return true;
}

switch (true) {

    // 4 or more G4 or higher
    case ((g[4] + g[5] + g[6]) >= 4):
        fsScore = [9.0, 10.0];
        break;

        // 3 or more G4 or higher
    case ((g[4] + g[5] + g[6]) >= 3):
        fsScore = [8.0, 9.0];
        break;

        // More than 1 G4 or pyramidal G5 alone. Higher combos will be caught above. 
    case (((g[4] + g[5] + g[6]) > 1) || (pyramidal == 5)):
        fsScore = [7.0, 8.0];
        break;

        // More than 2 G3 or higher
    case ((g[3] + g[4] + g[5] + g[6]) > 2):
        fsScore = [6.0, 6.5];
        break;

        // 1 G5 and rest G0 or G1
    case (compare(g, [">=0", ">=0", "==0", "==0", "==0", "==1", "==0"])):
        fsScore = [5.0, 5.5];
        break;

        // 1 G4 and rest G0 or G1
    case (compare(g, [">=0", ">=0", "==0", "==0", "==1", "==0", "==0"])):
        fsScore = [4.0, 4.5];
        break;

        // 1 G3 and 1 G2
        // 1 G3 and 2 G2
        // 2 G3 and rest G0 or G1 (** wording is ambig here)
        // 5 G2 and rest G0 or G1
    case (
        (compare(g, [">=0", ">=0", "==1", "==1", "==0", "==0", "==0"])) ||
        (compare(g, [">=0", ">=0", "==2", "==1", "==0", "==0", "==0"])) ||
        (compare(g, [">=0", ">=0", "==0", "==2", "==0", "==0", "==0"])) ||
        (compare(g, [">=0", ">=0", "==5", "==0", "==0", "==0", "==0"]))
    ):
        fsScore = [3.5, 3.5];
        break;

        // 1 G3, the rest G0 or G1
        // 3 G2, the rest G0 or G1
        // 4 G2, the rest G0 or G1
    case (
        (compare(g, [">=0", ">=0", "==0", "==1", "==0", "==0", "==0"])) ||
        (compare(g, [">=0", ">=0", "==3", "==0", "==0", "==0", "==0"])) ||
        (compare(g, [">=0", ">=0", "==4", "==0", "==0", "==0", "==0"]))
    ):
        fsScore = [3.0, 3.0];
        break;

        // 2 G2, the rest G0 or G1
    case (compare(g, [">=0", ">=0", "==2", "==0", "==0", "==0", "==0"])):
        fsScore = [2.5, 2.5];
        break;


        // 1 G2, the rest G0 or G1
    case (compare(g, [">=0", ">=0", "==1", "==0", "==0", "==0", "==0"])):
        fsScore = [2.0, 2.0];
        break;

        // More than 2 G1, the rest G0, if cerebral == 1
        // More than 1 G1, the rest G0, if cerebral == 0
    case (
        ((compare(g, [">=0", ">2", "==0", "==0", "==0", "==0", "==0"])) && (cerebral == 1)) ||
        ((compare(g, [">=0", ">1", "==0", "==0", "==0", "==0", "==0"])) && (cerebral === 0))
    ):
        fsScore = [1.5, 1.5];
        break;

        // 7 G0 and 1 G1 if cerebral == 0
        // 6 G0 and 2 G1 if cerebral == 1
    case (
        ((g[0] == 6) && (g[1] == 2) && (cerebral == 1)) ||
        ((g[0] == 7) && (g[1] == 1) && (cerebral === 0))
    ):
        fsScore = [1.0, 1.0];
        break;

        // 7 G0 and cerebral G1 or 8G0    
    case (
        ((g[0] == 7) && (g[1] == 1) && (cerebral == 1)) ||
        (g[0] == 8)
    ):
        fsScore = [0.0, 0.0];
        break;

        // If score falls to the bottom, there is too little information contained in EDSS to codify it based on FS. However, it is likely that the patient will have ambulatory findings that will inform the DSS and help the clinician arrive at the correct score. 
    default:
        fsScore = [4.0, 10.0];
        var uncertain = true;
}


if (fsScore[0] == fsScore[1]) {
    fsScore = fsScore[0];
} else {
    fsScore = String(fsScore[0]) + " to " + String(fsScore[1]);
}

mini_msg += score
msg += score;

if (ambulation >= 4) {
    var score = ambulation;
} else {
    var score = fsScore;
}

var mini_msg = "EDSS: " + score;
var msg = "Ambulation Score: " + ambulation + "<br/>" + "<br/>Functional Systems Score: " + fsScore;


if (uncertain == true) {
    msg += "<br/><br/>This combination of FS deficits is not explicitly defined in the EDSS. Please refer to the original text.";
}

var amb;
if (score <= 4.5) {
    amb = 'Normal'
} else{
    amb = 'Impaired'
}

calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: mini_msg
});
calc_output.push({
    name: '2146_EDSS/FSS for MS_result',
    value: score,
    value_text: 'points',
    message: msg
});
calc_output.push({
    name: '2146_EDSS/FSS for MS_interpretation',
    value: amb,
    value_text: '',
    message: 'Level of ambulation'
});
