data.logic:
var calc_output = [];
var u = parseInt(gender) + parseInt(age) + parseInt(fvc) + parseInt(dlco);
var stage = '';
var mort;
if (u >= 0 && u <= 3) {
    stage = 'I';
    mort = '5.6%';
}
if (u >= 4 && u <= 5) {
    stage = 'II';
    mort = '16.2%';
}
if (u >= 6 && u <= 8) {
    stage = 'III';
    mort = '39.2%';
}
var string_m = ' Mortality at 1 year.<br/><br/>See About section for 2- and 3-year estimates.';
var string_s = 'Stage ' + stage + ' IPF. ';
calc_output.push({
    name: 'mini',
    value: u.toFixed(0),
    value_text: 'points',
    message: string_s
});
calc_output.push({
    name: '2157_GAP Index for IPF_result',
    value: u.toFixed(0),
    value_text: 'points',
    message: string_s + '<br/><br/>' + mort + string_m
});
