data.logic:
var calc_output = [];
var msg = '';
var crit = '';
var mini_msg = '';
var msgAppend = '';
var u = parseInt(diseasesteps);
if (u == 0) { mini_msg = 'Normal';
    msg = 'Functionally normal with no limitations on activity or lifestyle.';
    msgAppend = ' Patients may have minor abnormality on examination, such as nystagmus or an extensor plantar. The course is relapsing-remitting with a return to baseline with or without treatment. These patients are not treated with any ongoing symptomatic therapy for MS.'; }
if (u == 1) { mini_msg = 'Mild Disability';
    msg = 'Mild symptoms or signs.';
    msgAppend = ' These patients have mild but definite findings such as sensory abnormalities, mild bladder impairment, minor incoordination, weakness, or fatigue. There is no visible abnormality of gait. The pattern of disease is relapsing remitting, but patients may not have a full return to baseline following attacks. These patients may use ongoing symptomatic therapy such as amantadine, baclofen, or oxybutynin.'; }
if (u == 2) { mini_msg = 'Moderate Disability';
    msg = 'The main feature is a visibly abnormal gait.';
    msgAppend = ' Patients do not require ambulation aids. The pattern of disease is relapsing-remitting or progressive.'; }
if (u == 3) { mini_msg = 'Early Cane';
    msg = 'Intermittent use of cane (or other forms of unilateral support including splint, brace, or crutch).';
    msgAppend = ' These patients use unilateral support primarily for longer distances, but are able to walk at least 25 feet without it. The pattern of disease is relapsing-remitting or progressive.'; }
if (u == 4) { mini_msg = 'Late Cane';
    msg = 'Patients are dependent on a cane or other forms of unilateral support.';
    msgAppend = ' Patients cannot walk 25 feet without such support (e.g., these patients may hang on to furniture inside their homes or touch the wall when walking in clinic). Patients may use a scooter for greater distances (e.g., malls). The pattern of disease is relapsing remitting or progressive.'; }
if (u == 5) { mini_msg = 'Bilateral Support';
    msg = 'Patients require bilateral support to walk 25 feet (e.g., two canes or two crutches or a walker).';
    msgAppend = ' They may use a scooter for greater distances. The pattern of disease is relapsing-remitting or progressive.'; }
if (u == 6) { mini_msg = 'Confined to Wheelchair';
    msg = 'Patients are essentially confined to a wheelchair or scooter.'
    msgAppend = ' They may be able to take a few steps but are unable to ambulate 25 feet, even with bilateral support. They may show further progression including worsening hand function or inability to transfer independently.'; }
if (u == 7) { mini_msg = 'Unclassifiable';
    msg = 'This category is used for patients who do not fit the above classification.';
    msgAppend = ' (e.g., significant cognitive or visual impairment, overwhelming fatigue, or significant bowel or bladder impairment in an otherwise minimally impaired patient).'; }

calc_output.push({
    name: 'mini', 
    value: '', 
    value_text: '', 
    message: mini_msg 
});
calc_output.push({ 
    name: '2199_Disease Steps for MS_result', 
    value: mini_msg, 
    value_text: '', 
    message: msg + msgAppend 
});
