data.logic:
var calc_output = [];
var age = parseFloat(age);
var ast = parseFloat(ast);
var plt = parseFloat(plt);
var alt = parseFloat(alt);

var u = (age * ast) / (plt * Math.sqrt(alt));
var msg, mini_msg;

u = u.toFixed(2);

if(age <= 35) {
    msg = 'Use alternative fibrosis assessment';
} else if(age > 35 && age < 65) {
    if (u < 1.3) {
        msg = "Advanced fibrosis excluded";
    } else if (u >= 1.3 && u <= 2.67) {
        msg = "Further investigation needed";
    } else if (u > 2.67) {
        msg = "Advanced fibrosis (METAVIR stage F3-F4) likely (McPherson 2017)";
    }
} else if (age >= 65) {
    if (u < 2.0) {
        msg = "Advanced fibrosis excluded";
    } else if (u >= 2.0 && u <= 2.67) {
        msg = "Further investigation needed";
    } else if (u > 2.67) {
        msg = "Advanced fibrosis (METAVIR stage F3-F4) likely (McPherson 2017)";
    }
}

var stage;
if (u < 1.45) {
  stage = 'Approximate fibrosis stage: Ishak 0-1 (Sterling et al 2006)';
} else if (u >= 1.45 && u <= 3.25) {
  stage = 'Approximate fibrosis stage: Ishak 2-3 (Sterling et al 2006)';
} else if (u > 3.25) {
  stage = 'Approximate fibrosis stage: Ishak 4-6 (Sterling et al 2006)';
} 

calc_output.push({
    name: 'mini',
    value: u,
    value_text: 'points',
    message: msg
});
calc_output.push({
    name: '2200_FIB-4_result',
    value: u,
    value_text: 'points',
    message: msg + '<br/>' + stage
});
