data.logic:
var calc_output = [];
var lymphs = parseFloat(lymph) / 100;
var unit, msg;
var v = parseFloat(wbc) * lymphs * 1000;
unit = "cells/mm\u00B3";
mini_msg = "Absolute Lymphocyte Count";

if (v < 1000) {
    msg = "If the patient is HIV+, he/she likely has AIDS (CD4 < 200 with 96% specificity).";
}
if (v >= 1000 && v < 2000) {
    msg = "Cannot reliably predict if CD4 is > or < 200.";
}
if (v >= 2000) {
    msg = "The patient likely does not have AIDS (CD4 > 200 with 90-97% sensitivity).";
}
calc_output.push({
    name: 'mini',
    value: v.toFixed(0),
    value_text: unit,
    message: mini_msg
});
calc_output.push({
    name: '2203_Absolute Lymphocyte Count_result',
    value: v.toFixed(0),
    value_text: unit,
    message: mini_msg + '<br/><br/>' + msg
});
