data.logic:
var calc_output = [];
var msg = '';
var mini_msg;
var webLanguage = webLanguage || "english"
var txt = '';
var u = parseFloat(ams) + parseFloat(rr) + parseFloat(sbp);
if (u < 2) {
    txt = '';
    mini_msg = 'Not high risk';

    if (webLanguage == "webProduction-chinese-traditional") {

        mini_msg = '不是高風險';

        msg = 'qSOFA分數評估風險不高，如果仍懷疑敗血症，應繼續監測病患，開始適當治療，並追蹤qSOFA分數變化';

    } else if (webLanguage == "webProduction-chinese-simplified") {
        mini_msg = '不是高风险';

        msg = 'qSOFA分数评估风险不高。如果仍怀疑败血症,应继续监测病患,开始适当治疗,并追踪qSOFA分数变化。 ';
    } else if (webLanguage == "webProduction-spanish") {
        mini_msg = 'SIN ALTO RIESGO'
        msg = 'Si todavía hay sospechas de una septicemia, continúe el monitoreo, la evaluación e inicie el tratamiento de manera apropiada, incluyendo aprobaciones rutinarias de qSOFA.'
    } else {

        msg = 'If sepsis is still suspected, continue to monitor, evaluate, and initiate treatment as appropriate, including serial qSOFA assessments.';
    }
}
if (u >= 2) {
    txt = '';
    mini_msg = 'High risk';

    if (webLanguage == "webProduction-chinese-traditional") {
        mini_msg = '高風險';
        msg = '根據qSOFA分級為高風險。應繼續評估其餘器官衰竭指標，血液檢測也應包括乳酸，並計算出完整的SOFA評分。<br/><br/>凡是qSOFA指標有達到的病患，即便之前沒有感染的事實';

    } else if (webLanguage == "webProduction-chinese-simplified") {
        mini_msg = '高风险';
        msg = '根据qSOFA分级为高风险。 应继续评估其余器官衰竭指针,血液检测也应包括乳酸,并计算出完整的SOFA评分。<br/><br/>凡是qSOFA指针有达到的病患，即便之前没有感染的事实';
    } else if (webLanguage == "webProduction-spanish") {
        mini_msg = 'ALTO RIESGO';
        msg = 'Evaluar evidencia por mal funcionamiento de órganos con examen de sangre incluyendo lactato sérico y cálculos completos de resultados qSOFA. Pacientes con estos criterios de qSOFA deberían de considerarse con infección, aunque no hubiera sido el caso anteriormente.';
    } else {

        msg = 'qSOFA Scores 2-3 are associated with a 3- to 14-fold increase in in-hospital mortality. Assess for evidence of organ dysfunction with blood testing including serum lactate and calculation of the full SOFA Score.<br/><br/>Patients meeting these qSOFA criteria should have infection considered even if it was previously not.';
    }

}

var value_text
var message

if (webLanguage == "webProduction-chinese-traditional") {
    value_text = "分"
    message = "qSOFA敗血症辨識工具"
} else if (webLanguage == "webProduction-chinese-simplified") {
    value_text = "分"
    message = "qSOFA败血症辨识工具"
} else if (webLanguage == "webProduction-spanish") {

    if (u == 1) {
        value_text = "punto"
    } else {
        value_text = "puntos"
    }

    message = "Puntaje SOFA"

} else {
    value_text = "points"
    message = "qSOFA Score"
}


calc_output.push({ 
    name: 'mini', 
    value: u, 
    value_text: value_text, 
    message: mini_msg 
});
calc_output.push({ 
    name: '2654_qSOFA_result', 
    value: u, 
    value_text: value_text,
    message: message 
});
calc_output.push({ 
    name: '2654_qSOFA_interpretation', 
    value: mini_msg, 
    value_text: '', 
    message: msg
});
