data.logic:
var calc_output = [];

var result
var msg = 'Assess for atropinization after 5 mins (clear lung exam, HR >80, sBP >80). If not atropinized, double previous dose and reassess in another 5 mins. Repeat doubling and reassessment every 5 mins until atropinization achieved, then begin continuous infusion at an hourly rate of 10% of the SUM of all doses.'

if (type == 0) {
    if (severity == 0) { // adult, mild 
        result = '1-2'
    } else if (severity == 1) { // adult, severe 
        result = '3-5'
    }
} else if (type == 1) { // peds
    if (weight <= 50) {
        result = (weight * 0.02).toFixed(2)
    } else {
        if (severity == 0) { // peds over 50 kg, mild 
            result = '1-2'
        } else if (severity == 1) { // peds over 50 kg, severe 
            result = '3-5'
        }
    }
}

var result = parseFloat(result)

//results
calc_output.push({
    name: 'mini',
    value: result,
    value_text: 'mg',
    message: 'First dose'
});
calc_output.push({
    name: '2664_Atropine Dosing_result',
    value: result,
    value_text: 'mg',
    message: 'First dose of atropine<br/><br/>' + msg
});
