data.logic:
var calc_output = [];
var inr = (parseFloat(inr) < 1) ? 1 : parseFloat(inr);
var bili = parseFloat(bilirubin);
var cr = parseFloat(creatinine);
if (UOMSYSTEM == true) {
    bili = bili;
    cr = cr;
    if (bili < 1) { bili = 1; }
    if (cr < 1) { cr = 1; }
}
if (UOMSYSTEM == false) {
    bili = bili / 17.104;
    cr = cr / 88.4; // these are now converted to US units, so now do a check to make sure they're not <1, otherwise set to 1 
    if (bili < 1) { bili = 1; }
    if (cr < 1) { cr = 1; }
}

cr = (dialysis == 1 || cr > 4) ? 4 : cr;
var value = 10 * ((.378 * Math.log(bili)) + (1.12 * Math.log(inr)) + (.957 * Math.log(cr)) + .643);
var mort = '';
if (value < 6) { value = 6; }

if (value < 9) { mort = '1.9%'; }
if (value > 9 && value < 20) { mort = '6.0%'; }
if (value > 19 && value < 30) { mort = '19.6%'; }
if (value > 29 && value < 40) { mort = '52.6%'; }
if (value > 39) { mort = '71.3%'; }

calc_output.push({ 
    name: 'mini', 
    value: value.toFixed(0), 
    value_text: 'points', 
    message: 'Original MELD Score (Pre-2016)*' 
});
calc_output.push({ 
    name: '2693_MELD Score (Original)_result (score)', 
    value: value.toFixed(0), 
    value_text: 'points', 
    message: 'Original MELD Score (Pre-2016)*' 
});
calc_output.push({ 
    name: '2693_MELD Score (Original)_result (mortality)', 
    value: mort, 
    value_text: ' ', 
    message: 'Estimated 3-Month Mortality' 
});
