data.logic:
var calc_output = [];
var weight = parseFloat(weight); // already defaults to kg
var bicarb = parseFloat(bicarbonate); // no conversion needed
var u = 0.4 * weight * (24 - bicarb);

if (UOMSYSTEM == true) { // US units, u already in US, no change
  calc_output.push({
    name: 'mini',
    value: u.toFixed(1),
    value_text: 'mEq',
    message: 'Bicarbonate Deficit'
  });
  calc_output.push({
    name: '27_Bicarbonate Deficit_result',
    value: u.toFixed(1),
    value_text: 'mEq',
    message: 'Bicarbonate Deficit'
  });

}

if (UOMSYSTEM == false) { // SI units, just change the units
  calc_output.push({
    name: 'mini',
    value: u.toFixed(1),
    value_text: 'mmol',
    message: 'Bicarbonate Deficit'
  });
  calc_output.push({
    name: '27_Bicarbonate Deficit_result',
    value: u.toFixed(1),
    value_text: 'mmol',
    message: 'Bicarbonate Deficit'
  });

}
