data.logic:
var t = t || 0
var n = n || 0
var m = m || 0

var calc_output = [];
var disease = parseInt(disease);
t = parseInt(t);
n = parseInt(n);
m = parseInt(m);
var fuhrman = parseInt(fuhrman);
var ecog = parseInt(ecog);
var survstring = ' Five Year survival';
var value = '';
var msg = '';
var u;

if (disease == 1) {
    u = t + fuhrman + ecog;
} else if (disease == 2) {
    u = n + m + fuhrman + ecog;
}

if (disease == 1) {
    if (t == 1 && fuhrman <= 2 && ecog == 0) {
        value = '91.1%';
        msg = 'Low Risk';
    } else if (t == 1 && fuhrman <= 2 && ecog == 1) {
        value = '80.4%';
        msg = 'Moderate Risk';
    } else if (t == 1 && fuhrman > 1 && fuhrman <= 4 && ecog >= 0) {
        value = '80.4%';
        msg = 'Moderate Risk';
    } else if (t == 2) {
        value = '80.4%';
        msg = 'Moderate Risk';
    } else if (t == 3 && fuhrman == 1 && ecog >= 0) {
        value = '80.4%';
        msg = 'Moderate Risk';
    } else if (t == 3 && fuhrman > 1 && ecog == 0) {
        value = '80.4%';
        msg = 'Moderate Risk';
    } else if (t == 3 && fuhrman > 1 && ecog >= 1) {
        value = '54.7%';
        msg = 'High Risk';
    } else if (t == 4) {
        value = '54.7%';
        msg = 'High Risk';
    } 
}
else if (disease == 2) {
    if (n == 1 && m == 0 && fuhrman > 0 && ecog >= 0) {
        value = '32%';
        msg = 'Low Risk';
    } else if (n == 2 && m >= 0 && fuhrman >= 1 && fuhrman <= 2 && ecog == 0) {
        value = '32%';
        msg = 'Low Risk';
    } else if (n == 2 && m >= 0 && fuhrman >= 1 && fuhrman <= 2 && ecog == 1) {
        value = '19.5%';
        msg = 'Moderate Risk';
    } else if (n == 1 && m >= 0 && fuhrman >= 1 && fuhrman <= 2 && ecog >= 0) {
        value = '19.5%';
        msg = 'Moderate Risk';
    } else if (n == 1 && m >= 0 && fuhrman >= 3 && fuhrman <= 4 && ecog >= 0) {
        value = '19.5%';
        msg = 'Moderate Risk';
    } else if (n == 2 && m >= 0 && fuhrman == 3 && ecog >= 0) {
        value = '19.5%';
        msg = 'Moderate Risk';
    } else if (n == 2 && m >= 0 && fuhrman == 4 && ecog == 0) {
        value = '19.5%';
        msg = 'Moderate Risk';
    } else if (n == 2 && m >= 0 && fuhrman == 4 && ecog == 1) {
        value = '0%';
        msg = 'High Risk';
    }
}

calc_output.push({ 
    name: 'mini', 
    value: msg, 
    value_text: ' ', 
    message: ' '
});
calc_output.push({ 
