var calc_output = [];

var dia = parseInt(dia);
var shape =  parseInt(shape);
var nucleoli =  parseInt(nucleoli);
var biz = parseInt(biz);

var grade, surv, msg;


if (dia == 1 && shape >= 1 && nucleoli >= 1) {grade = 'Grade I'; surv='96% Survival'; msg='<br/><br/>Per Original Study: Tumors classified as Nuclear Grade 1 did not metastasize for at least 5 years.';}

if (dia == 1 && shape == 1 && nucleoli == 1) {grade = 'Grade I'; surv='96% Survival'; msg='<br/><br/>Per Original Study: Tumors classified as Nuclear Grade 1 did not metastasize for at least 5 years.';}

