if (dia == 3 && shape >= 1 && nucleoli >= 1 && biz == 0) {grade = 'Grade III'; surv='59% Survival'; msg='';}

if (dia == 3 && shape == 3 && nucleoli ==3 && biz == 1) {grade = 'Grade IV'; surv='31% Survival'; msg='';}


