data.logic:
var calc_output = [];

var backpain = parseFloat(backpain)
var required = parseFloat(required)
var ibp = parseFloat(ibp)
var arthritis = parseFloat(arthritis)
var enthesitis = parseFloat(enthesitis)
var uveitis = parseFloat(uveitis)
var dactylitis = parseFloat(dactylitis)
var psoriasis = parseFloat(psoriasis)
var crohns = parseFloat(crohns)
var nsaids = parseFloat(nsaids)
var famhx = parseFloat(famhx)
var crp = parseFloat(crp)

if (required == 0) {
	var hla = parseFloat(hla)
}
else if (required == 1) {
	var hla = 0
}

var sum = ibp + arthritis + enthesitis + uveitis + dactylitis + psoriasis + crohns + nsaids + famhx + hla + crp

var result = ''
var msg = ''

if (backpain == 0) {
	result = 'Not axial SpA'
	msg = 'Entry criterion not fulfilled'
}
if (backpain == 1) {
	if (required == 0) { // SI on imaging
		if (sum >= 1) {
			result = 'Axial SpA'
			msg = 'Fulfills ASAS criteria for SpA diagnosis'
		}
		else if (sum < 1) {
			result = 'Not axial SpA'
			msg = 'ASAS criteria for SpA diagnosis not fulfilled'
		}
	}
	else if (required == 1) { // HLA positive
		if (sum >= 2) {
			result = 'Axial SpA'
			msg = 'Fulfills ASAS criteria for SpA diagnosis'
		}
		else if (sum < 2) {
			result = 'Not axial SpA'
			msg = 'ASAS criteria for SpA diagnosis not fulfilled'
		}
	}
}

calc_output.push({
   name: 'mini',
   value: result,
   value_text: '',
   message: ''
});
calc_output.push({
   name: '306_ASAS Criteria for Axial SpA_result',
   value: result,
   value_text: '',
   message: msg
});
