data.logic:
var calc_output = [];

var ast = parseFloat(ast);
var uln = parseFloat(uln);
var plt = parseFloat(plt);
var score = (ast / uln / plt) * 100;
var msg = '';
var mini_msg= '';
switch (true) {
    case (score < 0.7):
        msg = 'Per Lin et al (2011), scores < 0.7 were not sensitive or specific enough to determine level of fibrosis or cirrhosis.';
        mini_msg= 'Unable to determine severity.';
        break;
    case (score >= 0.7 && score < 1.0):
        msg = 'Per Lin et al (2011), scores ≥ 0.7:<br/><br/> 77% sensitive and 72% specific for significant fibrosis';
        mini_msg= 'Siginificant fibrosis';
        break;
    case (score >= 1.0 && score < 1.5):
        msg = "Per Lin et al (2011), scores ≥1:<br/><br/>61% sensitive and 64% specific for severe fibrosis<br/><br/>76% sensitive and 72% specific for cirrhosis";
        mini_msg= 'Severe fibrosis';
        break;
    case (score >= 1.5 && score < 2.0):
        msg = "Per Lin et al (2011), scores ≥1.5:<br/><br/>27% sensitive and 93% specific for significant fibrosis<br/>br/>50% sensitive and 84% specific for severe fibrosis<br/><br/>scores ≥1:<br/>76% sensitive and 72% specific for cirrhosis";
        mini_msg= 'Significant fibrosis';
        break;
    case (score >= 2.0):
        msg = "Per Lin et al (2011), scores ≥2:<br/><br/>36% sensitive and 93% specific for severe fibrosis<br/><br/>46% sensitive and 91% specific for cirrhosis";
        mini_msg= 'Severe fibrosis';
        break;
    default:
        console.log('error')
};
calc_output.push({
    name: 'mini',
    value: score.toFixed(1),
    value_text: 'points',
    message: mini_msg
});
calc_output.push({
    name: '3094_APRI_result',
    value: score.toFixed(1),
    value_text: 'points',
    message: msg
});
