data.logic:
var calc_output = [];

var age = parseFloat(age)
var sex = parseFloat(sex)

var esr

var crp

if (sex == 1){
	esr = (age + 10) / 2
	crp = (age / 50) + 0.6
}
else {
	esr = age / 2
	crp = age / 50
}

calc_output.push({
    name: 'mini',
    value: esr.toFixed(1) + ', ' + crp.toFixed(1),
    value_text: '',
    message: 'ESR, CRP'
});

calc_output.push({
   name: '10049_Age-Adjusted ESR/CRP for RA_result (ESR)',
   value: esr.toFixed(1),
   value_text: 'mm/hr',
   message: 'ESR upper limit of normal'
});

calc_output.push({
   name: '10049_Age-Adjusted ESR/CRP for RA_result (CRP)',
   value: crp.toFixed(1),
   value_text: 'mg/dL',
   message: 'CRP upper limit of normal'
});
