data.logic:
var calc_output = [];

if (UOMSYSTEM == true) { // US units
    var serum = parseFloat(serum);
    var ascitic = parseFloat(ascitic);
}

if (UOMSYSTEM == false) { // SI units so convert to US units
    var serum = parseFloat(serum) / 10;
    var ascitic = parseFloat(ascitic)/10;
}


var u = serum - ascitic;
var msg = '';
var mini_msg = '';

if (u < 1.1) { mini_msg = 'Ascites not due by portal hypertension';
    msg = 'SAAG < 1.1 g/dL<br/>Portal hypertension is not necessarily the cause of ascites.';
} else if (u >= 1.1) { mini_msg = 'Ascites likely due by portal hypertension';
    msg = 'SAAG > 1.1 g/dL<br/>Portal hypertension is likely the cause of ascites, with 97% accuracy.';
}
    calc_output.push({
        name: 'mini',
        value: u.toFixed(1),
        value_text: 'g/dL',
        message: mini_msg
    });
    calc_output.push({
        name: '3096_Serum Ascites Albumin Gradient_result',
        value: u.toFixed(1),
        value_text: 'g/dL',
        message: msg
    });
