data.logic:
var calc_output = [];

var webLanguage = webLanguage || "english"


if (UOMSYSTEM == true) { // US units
    var normal_albumin = parseFloat(normal_albumin);
    var albumin = parseFloat(albumin);
    var calcium = parseFloat(calcium);

}

if (UOMSYSTEM == false) { // SI units so convert to US units
    var normal_albumin = parseFloat(normal_albumin) / 10;
    var albumin = parseFloat(albumin) / 10;
    var calcium = parseFloat(calcium) / 0.25;

}

// units are now all in US units, so only one equation is needed

var u, units;
u = (0.8 * (normal_albumin - albumin) + calcium);
units = '';

if (UOMSYSTEM == true) { // US units, u already in US, no change
    units = 'mg/dL';
} else if (UOMSYSTEM == false) { // need US units, convert u and units unit
    u = u * 0.25;
    units = 'mmol/L';
}



var message = "";
if (webLanguage == "webProduction-chinese-traditional") {

    message = 'æ ¡æ­£éé£é¢å­';

} else if (webLanguage == "webProduction-chinese-simplified") {

    message = 'æ ¡æ­£è¿éç¦»å­';

} else if (webLanguage == "webProduction-spanish") {

    message = 'Calcio corregido';

} else {

    message = 'Corrected Calcium';
}

if (UOMSYSTEM == true) {
  	var siconversion = u * 0.25;
	var conversion_text = 'Equivalent to ' + siconversion.toFixed(1) + ' mmol/L';
}

if (UOMSYSTEM == false) {
	var usconversion = u * 4;
	var conversion_text = 'Equivalent to ' + usconversion.toFixed(1) + ' mg/dL';
}

calc_output.push({
    name: 'mini',
    value: u.toFixed(1),
    value_text: units,
    message: message
});
calc_output.push({
    name: '31_Ca Correction for Albumin_result',
    value: u.toFixed(1),
    value_text: units,
    message: message  + '<br/><br/>' + conversion_text
});
