data.logic:
var calc_output = [];

if (UOMSYSTEM == true) { // US units
    var glucose = parseFloat(glucose);
    var insulin = parseFloat(insulin);
}

if (UOMSYSTEM == false) { // SI units so convert to US units
    var glucose = parseFloat(glucose) / 0.0555;
    var insulin = parseFloat(insulin)/6
}

var u = (insulin * glucose);
v = u / 405;

msg = 'Several studies suggest a cut-off of >2 for any insulin resistance, but "normal" values appear to vary greatly by population.';
mini_msg = '>2 points suggest insulin resistance';

    calc_output.push({
        name: 'mini',
        value: v.toFixed(1),
        value_text: '',
        message: mini_msg
    });
    calc_output.push({
        name: '3120_HOMA-IR_result',
        value: v.toFixed(1),
        value_text: '',
        message: msg
    });
