data.logic:
var calc_output = [];
var msg = '';
var level = '';
var c = parseFloat(edbeds);
var d = parseFloat(totalpts);
var e = parseFloat(vents);
var f = parseFloat(waitingtime_admit);
var g = parseFloat(hospitalbeds);
var h = parseFloat(admits);
var i = parseFloat(waitingtime_wr);
if (e >= 2) { e = 2; }
var gtot = (-20 + (d / c) * 85.8 + (h / g) * 600 + e * 13.4 + f * 0.93 + i * 5.64);
if (gtot >= 200) { gtot = 200; }
if (gtot < 21) {
    level = 'Level 1';
    msg = 'Not busy';
}
if (gtot > 20 && gtot < 61) {
    level = 'Level 2';
    msg = 'Busy';
}
if (gtot > 60 && gtot < 101) {
    level = 'Level 3';
    msg = 'Extremely busy but not overcrowded';
}
if (gtot > 100 && gtot < 141) {
    level = 'Level 4';
    msg = 'Overcrowded';
}
if (gtot > 140 && gtot < 181) {
    level = 'Level 5';
    msg = 'Severely overcrowded';
}
if (gtot > 180) {
    level = 'Level 6';
    msg = 'Dangerously overcrowded';
}

calc_output.push({
    name: 'mini',
    value: level,
    value_text: '('+gtot.toFixed(0)+')',
    message: msg
});
calc_output.push({
    name: '3143_NEDOCS_result',
    value: level,
    value_text: '('+gtot.toFixed(0)+')',
    message: msg
});
