data.logic:
var calc_output = [];

var msg = '';
var level = '';
var value = 33.3 * (0.11 + (0.07 * parseFloat(waitingpts)) + (0.04 * parseFloat(totalpts)));
var gtot = value;
if (gtot < 21) {
    level = 'Level 1';
    msg = 'Not Busy';
}
if (gtot > 20 && gtot < 61) {
    level = 'Level 2';
    msg = 'Busy';
}
if (gtot > 60 && gtot < 101) {
    level = 'Level 3';
    msg = 'Extremely busy but not overcrowded';
}
if (gtot > 100 && gtot < 141) {
    level = 'Level 4';
    msg = 'Overcrowded';
}
if (gtot > 140 && gtot < 181) {
    level = 'Level 5';
    msg = 'Severely overcrowded';
}
if (gtot > 180) {
    level = 'Level 6';
    msg = 'Dangerously overcrowded';
}

 calc_output.push({
     name: 'mini',
     value: level,
     value_text: '('+gtot.toFixed(0)+')',
     message: msg
 });
 calc_output.push({
     name: '3144_PEDOCS_result',
     value: level,
     value_text: '('+gtot.toFixed(0)+')',
     message: msg
 });
